import React, { useCallback, useEffect, useState } from 'react';
import AddProjectModal from '../modals/AddProjectModal';
import axios from 'axios';
import { Link } from 'react-router-dom';
import { toast } from 'react-hot-toast';

const Sidebar = () => {
  const [isModalOpen, setModalState] = useState(false);
  const [projects, setProjects] = useState([]);
  const [isLoading, setIsLoading] = useState(false);
  const [paramsWindow, setParamsWindow] = useState(window.location.pathname.slice(1));

  const openModal = useCallback(() => {
    setModalState(true);
  }, []);

  const closeModal = useCallback(() => {
    setModalState(false);
  }, []);

  const fetchProjects = useCallback(async () => {
    setIsLoading(true);
    try {
      const response = await axios.get('http://localhost:9000/projects/');
      setProjects(response.data);
    } catch (error) {
      toast.error('Failed to fetch projects. Please try again.');
      console.error('Error fetching projects:', error);
    } finally {
      setIsLoading(false);
    }
  }, []);

  useEffect(() => {
    fetchProjects();

    const handleProjectUpdate = () => {
      fetchProjects();
    };

    document.addEventListener('projectUpdate', handleProjectUpdate);
    return () => {
      document.removeEventListener('projectUpdate', handleProjectUpdate);
    };
  }, [fetchProjects]);

  const handleLocation = (e) => {
    setParamsWindow(new URL(e.currentTarget.href).pathname.slice(1));
  };

  return (
    <div className='py-5'>
      <div className="px-4 mb-3 flex items-center justify-between">
        <span className=''>Projects</span>
        <button
          onClick={openModal}
          className='bg-indigo-200 rounded-full p-[2px] focus:outline-none focus:ring focus:ring-indigo-200 focus:ring-offset-1'
          aria-label="Add Project"
        >
          <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24" fill="currentColor" className="w-5 h-5 text-indigo-600">
            <path fillRule="evenodd" d="M12 5.25a.75.75 0 01.75.75v5.25H18a.75.75 0 010 1.5h-5.25V18a.75.75 0 01-1.5 0v-5.25H6a.75.75 0 010-1.5h5.25V6a.75.75 0 01.75-.75z" clipRule="evenodd" />
          </svg>
        </button>
      </div>
      <ul className='border-r border-gray-300 pr-2'>
        {isLoading ? (
          <li className="px-5 py-1.5 text-gray-600 text-sm">Loading projects...</li>
        ) : projects.length > 0 ? (
          projects.map((project, index) => (
            <Link key={index} to={project._id} onClick={handleLocation}>
              <li className={`px-5 py-1.5 mb-1 text-gray-600 font text-sm capitalize select-none hover:text-indigo-600 rounded transition-colors hover:bg-indigo-200/80 ${paramsWindow === project._id && 'text-indigo-600 bg-indigo-200/80'}`}>
                {project.title}
              </li>
            </Link>
          ))
        ) : (
          <li className="px-5 py-1.5 text-gray-600 text-sm">No projects found.</li>
        )}
      </ul>
      <AddProjectModal isModalOpen={isModalOpen} closeModal={closeModal} />
    </div>
  );
};

export default Sidebar;
