import { BrowserRouter, Routes, Route, Link } from 'react-router-dom';
import { AuthProvider } from './components/AuthContext';
import Login from './components/Login';
import Signup from './components/Signup';
import AppLayout from "./components/AppLayout";
import Task from "./components/Task";
import { Toaster } from "react-hot-toast";

function App() {
  console.log('render app..');

  return (
    <AuthProvider>
      <AppLayout>
        <Toaster position="top-right" gutter={8} />

        <nav className="p-4 bg-gray-800 text-white fixed top-0 left-0 w-full z-10">
          <div className="flex justify-center">
            <Link to="/" className="mx-4 hover:text-gray-300">Home</Link>
            <Link to="/login" className="mx-4 hover:text-gray-300">Login</Link>
            <Link to="/signup" className="mx-4 hover:text-gray-300">Signup</Link>
          </div>
        </nav>

        <div >
        <Routes>
          <Route path="/login" element={<Login />} />
          <Route path="/signup" element={<Signup />} />
          <Route path="/:projectId" element={<Task />} />
          <Route path="/" element={
            <div className="flex flex-col items-center w-full pt-10">
              <img src="./image/welcome.svg" className="w-5/12" alt="Welcome" />
              <h1 className="text-lg text-gray-600">Select or create a new project</h1>
            </div>
          } />
        </Routes>
        </div>


      </AppLayout>
    </AuthProvider>
  );
}

export default App;
