import { BrowserRouter, Routes, Route, Link } from 'react-router-dom';
import { AuthProvider } from './components/AuthContext';
import Login from './components/Login';
import Signup from './components/Signup';
import AppLayout from "./components/AppLayout";
import Task from "./components/Task";
import { Toaster } from "react-hot-toast";

function App() {
  console.log('render app..');

  return (
    <AuthProvider>
      <AppLayout>
        <Toaster position="top-right" gutter={8} />

        {/* Navbar */}
        <nav className="p-4 bg-gray-800 text-white fixed top-0 left-0 w-full z-10 shadow-md">
          <div className="max-w-7xl mx-auto flex justify-center space-x-8">
            <Link to="/" className="hover:text-gray-300 text-lg">Home</Link>
            <Link to="/login" className="hover:text-gray-300 text-lg">Login</Link>
            <Link to="/signup" className="hover:text-gray-300 text-lg">Signup</Link>
          </div>
        </nav>

        {/* Main Content */}
        <div className="pt-20 min-h-screen bg-gray-900 text-white">
          <div className="container mx-auto px-6 lg:px-20">
            <Routes>
              <Route path="/login" element={
                <div className="flex items-center justify-center min-h-[80vh]">
                  <div className="w-full max-w-lg">
                    <Login />
                  </div>
                </div>
              } />
              <Route path="/signup" element={
                <div className="flex items-center justify-center min-h-[80vh]">
                  <div className="w-full max-w-lg">
                    <Signup />
                  </div>
                </div>
              } />
              <Route path="/:projectId" element={<Task />} />
              <Route path="/" element={
                <div className="flex flex-col items-center justify-center min-h-[80vh]">
                  <img 
                    src="./image/welcome.svg" 
                    className="w-5/12 max-w-xl mb-6" 
                    alt="Welcome" 
                  />
                  <h1 className="text-2xl font-bold text-gray-300">Select or create a new project</h1>
                </div>
              } />
            </Routes>
          </div>
        </div>

      </AppLayout>
    </AuthProvider>
  );
}

export default App;
