import { BrowserRouter, Routes, Route, Link } from 'react-router-dom';
import { AuthProvider } from './components/auth/AuthContext';
import { lazy, Suspense } from 'react';
import { Toaster } from 'react-hot-toast';
import Navbar from './components/navigation/Navbar';
import AuthLayout from './components/auth/AuthLayout';
import AppLayout from './components/auth/AppLayout';
import ErrorFallback from './components/ErrorFallback';
import { ErrorBoundary } from 'react-error-boundary';

const Login = lazy(() => import('./components/auth/Login'));
const Signup = lazy(() => import('./components/auth/Signup'));
const Task = lazy(() => import('./components/Task'));

function App() {
  return (
    <ErrorBoundary FallbackComponent={ErrorFallback}>
      <AuthProvider>
        <AppLayout>
          <Toaster position="top-right" gutter={8} />
          <Navbar />
          <div className="pt-20 min-h-screen bg-gray-900 text-white flex justify-center items-center">
            <div className="container mx-auto px-6">
              <Suspense fallback={<div>Loading...</div>}>
                <Routes>
                  <Route path="/login" element={
                    <AuthLayout>
                      <Login />
                    </AuthLayout>
                  } />
                  <Route path="/signup" element={
                    <AuthLayout>
                      <Signup />
                    </AuthLayout>
                  } />
                  <Route path="/:projectId" element={<Task />} />
                  <Route path="/" element={
                    <div className="flex flex-col items-center justify-center min-h-[80vh]">
                      <img 
                        src="./image/welcome.svg" 
                        className="w-5/12 max-w-xl mb-6" 
                        alt="Welcome" 
                      />
                      <h1 className="text-2xl font-bold text-gray-300">Select or create a new project</h1>
                    </div>
                  } />
                </Routes>
              </Suspense>
            </div>
          </div>
        </AppLayout>
      </AuthProvider>
    </ErrorBoundary>
  );
}

export default App;
