import { Link } from 'react-router-dom';
import { useContext } from 'react';
import AuthContext from './auth/AuthContext';

const Navbar = () => {
  const { user, logout } = useContext(AuthContext);

  return (
    <nav className="p-4 bg-gray-800 text-white fixed top-0 left-0 w-full z-10 shadow-md">
      <div className="max-w-7xl mx-auto flex justify-center items-center space-x-8">
        <Link to="/" className="hover:text-gray-300 text-lg" aria-label="Home">
          Home
        </Link>

        {user ? (
          <>
            <Link to="/dashboard" className="hover:text-gray-300 text-lg" aria-label="Dashboard">
              Dashboard
            </Link>
            <button
              onClick={logout}
              className="hover:text-gray-300 text-lg"
              aria-label="Logout"
            >
              Logout
            </button>
          </>
        ) : (
          <>
            <Link to="/login" className="hover:text-gray-300 text-lg" aria-label="Login">
              Login
            </Link>
            <Link to="/signup" className="hover:text-gray-300 text-lg" aria-label="Signup">
              Signup
            </Link>
          </>
        )}
      </div>
    </nav>
  );
};

export default Navbar;
